import React, { useEffect, useState } from "react";
import NewsItem from "./NewsItem";
import Loading from "./Loading";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";
import   parser  from 'fast-xml-parser';
import { validate } from 'fast-xml-parser';
import { parseString } from 'xml2js';


import he from 'he';

const News = (props) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };


  // ...

  const updateNews = async () => {
    props.setProgress(10);
    const url = 'http://localhost:3001/rss';
    setLoading(true);
    props.setProgress(30);

    try {
      const response = await fetch(url);
      const xmlData = await response.text();

      parseString(xmlData, (err, result) => {
        if (err) {
          console.error('Error parsing XML:', err);
          setLoading(false);
        } else {
          const parsedData = result; // Parsed JSON data
          console.log(parsedData, '====parsedData===');
          props.setProgress(70);
          setArticles(parsedData.articles);
          setTotalResults(parsedData.totalResults);
          setLoading(false);
          props.setProgress(100);
        }
      });
    } catch (error) {
      console.error('Error fetching news:', error);
      setLoading(false);
    }
  };



  useEffect(() => {
    document.title = `${capitalizeFirstLetter(props.category)} - NewsHub`;
    updateNews();
  }, []);

  const fetchMoreData = async () => {
    setPage(page + 1);
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page + 1}&pagesize=${props.pagesize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    setArticles(articles.concat(parsedData.articles));
    setTotalResults(parsedData.totalResults);
  };
  return (
    <div>
      <h1 className="text-center" style={{ marginTop: "90px" }}>
        NewsHub - Top Headlines - {capitalizeFirstLetter(props.category)}
      </h1>
      {loading && <Loading />}
      <InfiniteScroll dataLength={articles.length} next={fetchMoreData} hasMore={articles.length !== totalResults} loader={<Loading />}>
        <div className="container">
          <div className="row">
            {articles.map((element, index) => {
              return (
                <div className="col-md-4" key={index}>
                  <NewsItem title={element.title} description={element.description} imgUrl={element.urlToImage} url={element.url} author={element.author} publishedAt={element.publishedAt} name={element.source.name} />
                </div>
              );
            })}
          </div>
        </div>
      </InfiniteScroll>
    </div>
  );
};

News.defaultProps = {
  country: "in",
  pagesize: 5,
  category: "genereal",
};
News.propTypes = {
  country: PropTypes.string,
  pagesize: PropTypes.number,
  category: PropTypes.string,
};

export default News;
